mapper.insert:

  SELECT LAST_INSERT_ID() AS ID

insert into commodity (title,price,classification,publisherName,description,contact,time)
values (#{title},#{price},#{classification},#{publisherName},#{description},#{concater},UNIX_TIMESTAMP());

mapper.select:
SELECT * FROM commodity WHERE 1 = 1

    AND title LIKE (SELECT CONCAT('%',#{search},'%')) OR description LIKE (SELECT CONCAT('%',#{search},'%'))


    AND classification in (SELECT CONCAT('(',#{classification},')'))

AND publisherName IN
(SELECT id FROM user WHERE 1 = 1


        AND institute_id in (SELECT CONCAT('(',#{instituteId},')'))

)
ORDER BY time DESC

    LIMIT #{pageInfo.start}, #{pageInfo.pageSize}
;
